package String;

public class ConvertStringIntoChar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			String s1="nagesh@gmail.com";
			
			for(int i=0;i<s1.length();i++)
			{
				char ch=s1.charAt(i);
				System.out.print(ch);
				
				
				//System.out.print(s1.charAt(i));
				
			}
	}

}
